import { useContext } from "react";
import CartContext from "../../store/cart-context";
import Card from "../UI/Card";
import classes from "./AvailableMeals.module.css";
import MealItem from "./MealItems/MealItem";

// const DUMMY_MEALS = [
//   {
//     id: "m1",
//     name: "Sushi",
//     description: "Finest fish and veggies",
//     price: 22.99,
//   },
//   {
//     id: "m2",
//     name: "Schnitzel",
//     description: "A german specialty!",
//     price: 16.5,
//   },
//   {
//     id: "m3",
//     name: "Barbecue Burger",
//     description: "American, raw, meaty",
//     price: 12.99,
//   },
//   {
//     id: "m4",
//     name: "Green Bowl",
//     description: "Healthy...and green...",
//     price: 18.99,
//   },
// ];

const AvailableMeals = async (props) => {
  const cartCtx = useContext(CartContext);

  let fetchMeals = async () => {
    try {
      let request = await fetch(
        "https://jstest-47ca2-default-rtdb.europe-west1.firebasedatabase.app/Meals.json"
      );

      let response = await request.json();

      let mealsItems = [];

      for (let key in response) {
        mealsItems.push({
          id: key,
          name: response[key].name,
          description: response[key].description,
          price: response[key].price,
        });
      }
      return mealsItems;
    } catch {
      return {};
    }
  };

  let meals = await fetchMeals();
  console.log();

  let onAddProduct = (id, amount) => {
    let item = meals.findIndex((meal) => meal.id === id);
    item.amount = amount;
    cartCtx.addItem(item);
  };

  return (
    <section className={classes.meals}>
      <Card>
        <ul>
          <MealItem addMealHandler={onAddProduct} meals={meals} />
        </ul>
      </Card>
    </section>
  );
};

export default AvailableMeals;
