import { useReducer } from "react";

import CartContext from "./cart-context";

const defaultCartState = {
  items: [],
  totalAmount: 0,
};

const productsReducer = (state, action) => {
  // TODO Make Amount Work
  if (action.type === "ADD") {
    const updatedTotalAmount =
      state.totalAmount + action.item.price * action.item.amount;

    const existingCartItemIndex = state.items.findIndex(
      (item) => item.id === action.item.id
    );
    const existingCartItem = state.items[existingCartItemIndex];
    let updatedItems;

    if (existingCartItem) {
      const updatedItem = {
        ...existingCartItem,
        amount: existingCartItem.amount + action.item.amount,
      };
      updatedItems = [...state.items];
      updatedItems[existingCartItemIndex] = updatedItem;
    } else {
      updatedItems = state.items.concat(action.item);
    }

    return defaultCartState;
  }
  if (action.type === "REMOVE") {
    let product = state.products.find((el) => el.id === action.removeId);
    if (product.count === 1) {
      state.totalAmount -= product.price;
      state.products = state.products.filter((el) => el.id !== action.removeId);
    } else {
      product.count -= 1;
      state.totalAmount -= product.price;
    }
  }
  return {
    items: state.products,
    totalAmount: state.totalAmount,
  };
};

const CartProvider = (props) => {
  const [addedProducts, dispatchAdddedProducts] = useReducer(
    productsReducer,
    defaultCartState
  );

  const AddProductHandler = (product) => {
    dispatchAdddedProducts({ item: product, type: "ADD" });
    console.log(addedProducts.totalAmount.toFixed(2));
  };

  const RemoveProductHandler = (id) => {
    dispatchAdddedProducts({ removeId: id, type: "REMOVE" });
  };

  const currentContext = {
    items: addedProducts.products,
    totalAmount: addedProducts.totalAmount,
    addItem: AddProductHandler,
    removeItem: RemoveProductHandler,
  };
  return (
    <CartContext.Provider value={currentContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
