import { useState } from "react";
import useHttp from "../../../hooks/use-http";
import classes from "./checkout.module.css";

const Checkout = (props) => {
  let [formIsValid, setIsFormValid] = useState(false);

  let {
    enteredValue: name,
    enteredValueHandler: nameChangeHandler,
    blurHandler: nameBlurHandler,
    reset: nameReset,
    hasError: nameHasError,
  } = useHttp((value) => {
    return value.length > 3;
  });
  let {
    enteredValue: street,
    enteredValueHandler: streetChangeHandler,
    blurHandler: streetBlurHandler,
    reset: streetReset,
    hasError: streetHasError,
  } = useHttp((value) => {
    return value.length > 3;
  });
  let {
    enteredValue: postalCode,
    enteredValueHandler: postalCodeChangeHandler,
    blurHandler: postalCodeBlurHandler,
    reset: postalCodeReset,
    hasError: postalCodeHasError,
  } = useHttp((value) => {
    return value.length > 3;
  });
  let {
    enteredValue: city,
    enteredValueHandler: cityChangeHandler,
    blurHandler: cityBlurHandler,
    reset: cityReset,
    hasError: cityHasError,
  } = useHttp((value) => {
    return value.length > 3;
  });

  if (
    !streetHasError &&
    !nameHasError &&
    !postalCodeHasError &&
    !cityHasError
  ) {
    setIsFormValid(true);
  }

  const submitHandler = (event) => {
    event.preventDefault();

    if (!formIsValid) {
      nameBlurHandler();
      streetBlurHandler();
      postalCodeBlurHandler();
      cityBlurHandler();
      setIsFormValid(false);
      console.log("work");
      return;
    }

    console.log(name, street, postalCode, city);
    formIsValid = false;

    nameReset();
    streetReset();
    postalCodeReset();
    cityReset();
  };

  return (
    <form className={classes.form} onSubmit={submitHandler}>
      <div className={classes.control}>
        {nameHasError && <p className={classes["error-msg"]}>Error</p>}
        <label htmlFor="name">Your Name</label>
        <input
          onChange={nameChangeHandler}
          onBlur={nameBlurHandler}
          type="text"
          id="name"
          value={name}
        />
      </div>
      <div className={classes.control}>
        {streetHasError && <p className={classes["error-msg"]}>Error</p>}
        <label htmlFor="street">Street</label>
        <input
          onChange={streetChangeHandler}
          onBlur={streetBlurHandler}
          type="text"
          id="street"
          value={street}
        />
      </div>
      <div className={classes.control}>
        {postalCodeHasError && <p className={classes["error-msg"]}>Error</p>}

        <label htmlFor="postal">Postal Code</label>
        <input
          onChange={postalCodeChangeHandler}
          onBlur={postalCodeBlurHandler}
          type="text"
          id="postal"
          value={postalCode}
        />
      </div>
      <div className={classes.control}>
        {cityHasError && <p className={classes["error-msg"]}>Error</p>}

        <label htmlFor="city">City</label>
        <input
          onChange={cityChangeHandler}
          onBlur={cityBlurHandler}
          type="text"
          id="city"
          value={city}
        />
      </div>
      <div className={classes.actions}>
        <button
          type="button"
          onClick={() => {
            props.onCancel();
            props.visibleFormHandler();
          }}
        >
          Cancel
        </button>
        <button className={classes.submit}>Confirm</button>
      </div>
    </form>
  );
};

export default Checkout;
