import { useState } from "react";

let [isOpenCart, setIsOpnenCart] = useState(true);

export let openCartHandler = () => {
  if (isOpenCart) {
    setIsOpnenCart(false);
  } else {
    setIsOpnenCart(true);
  }

  return isOpenCart;
};
