import { useReducer } from "react";

import CartContext from "./cart-context";

const productsReducer = (state, action) => {
  // TODO Make Amount Work
  if (!state.products.includes(action.product) && action.type === "ADD") {
    state.products.unshift(action.product);
    state.totalAmount += action.product.price * action.product.count;
  } else if (action.type === "REMOVE") {
    let product = state.products.find((el) => el.id === action.removeId);
    if (product.count === 1) {
      state.products = state.products.filter((el) => el.id !== action.removeId);
    } else {
      product.count -= 1;
      state.totalAmount -= product.amount;
    }
  }
  return {
    products: state.products,
    totalAmount: state.totalAmount,
  };
};

const CartProvider = (props) => {
  const [addedProducts, dispatchAdddedProducts] = useReducer(productsReducer, {
    products: [],
    totalAmount: 0,
  });

  const AddProductHandler = (product) => {
    dispatchAdddedProducts({ product: product, type: "ADD" });
    console.log(addedProducts.totalAmount.toFixed(2));
  };

  const RemoveProductHandler = (id) => {
    dispatchAdddedProducts({ removeId: id, type: "REMOVE" });
  };

  const currentContext = {
    items: addedProducts.products,
    totalAmount: addedProducts.totalAmount,
    addItem: AddProductHandler,
    removeItem: RemoveProductHandler,
  };
  return (
    <CartContext.Provider value={currentContext}>
      {props.children}
    </CartContext.Provider>
  );
};

export default CartProvider;
