import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  let [movies, setMovies] = useState([]);

  async function requestHanler() {
    let request = await fetch("https://swapi.dev/api/films/");
    let data = request.json();
    setMovies(data);
    console.log(movies);
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={requestHanler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
