import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";

function App() {
  let [movies, setMovies] = useState([]);
  let [isLoading, setLoading] = useState(false);

  async function requestHanler() {
    setLoading(true);
    try {
      let response = await fetch("https://swapi.dev/api/");
      let data = await response.json();
      let newMovies = data.results.map((film) => {
        return {
          id: film.episode_id,
          title: film.title,
          releaseDate: film.release_date,
          openingText: film.opening_crawl,
        };
      });
      setMovies(newMovies);
      setLoading(false);
    } catch {
      setLoading(false);
    }
  }

  return (
    <React.Fragment>
      <section>
        <button onClick={requestHanler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && movies.length === 0 && <p>No results!</p>}
        {!isLoading && movies.length > 0 && <MoviesList movies={movies} />}
        {isLoading && <p>Loading...</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
