import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";
import AddMovie from "./components/AddMovie";

function App() {
  let [movies, setMovies] = useState([]);
  let [isLoading, setLoading] = useState(false);
  let [error, setError] = useState(null);

  async function requestHanler() {
    setLoading(true);
    try {
      let response = await fetch("https://swapi.dev/api/filmsaa/");
      let data = await response.json();
      let newMovies = data.results.map((film) => {
        return {
          id: film.episode_id,
          title: film.title,
          releaseDate: film.release_date,
          openingText: film.opening_crawl,
        };
      });
      setMovies(newMovies);
    } catch (error) {
      setError(error.message);
    }
    setLoading(false);
  }

  let addMovieHandler = async (props) => {
    let response = await fetch("", {
      method: "POST",
    });
  };

  let content = <p>No results!</p>;

  if (!isLoading && movies.length > 0) {
    content = <MoviesList movies={movies} />;
  }

  if (isLoading) {
    content = <p>Loading...</p>;
  }

  if (error) {
    content = <p>{error}</p>;
  }
  return (
    <React.Fragment>
      <section>
        <AddMovie onAddMovie={addMovieHandler} />
      </section>
      <section>
        <button onClick={requestHanler}>Fetch Movies</button>
      </section>
      <section>{content}</section>
    </React.Fragment>
  );
}

export default App;
