import { Fragment } from "react";
import { json, Link, useLoaderData, useParams } from "react-router-dom";
import EventItem from "../components/EventItem";

let EventDetails = () => {
  const event = useParams().eventId;
  let loader = useLoaderData(event);
  console.log(loader);
  return <EventItem />;
};

export default EventDetails;

const eventLoader = async (eventId) => {
  let request = await fetch("http://localhost:8080/events" + eventId);

  if (!request.ok) {
    throw json({ message: "Event is Not Found!" }, { status: 500 });
  } else {
    return request;
  }
};
