import { createSlice } from "@reduxjs/toolkit";

let defaultState = {
  isOpen: false,
  products: [],
};

let cartSlice = createSlice({
  name: "cart",
  initialState: defaultState,
  reducers: {
    changeView(state) {
      state.isOpen = !state.isOpen;
    },
  },
});

export default cartSlice;
