import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { cartActions } from "./store";

let isInitial = true;

function App() {
  let isCartOpen = useSelector((state) => state.cart.isOpen);
  let cart = useSelector((state) => state.cart.items);
  let notification = useSelector((state) => state.ui);
  let dispatch = useDispatch();

  useEffect(() => {
    let sendCartData = async () => {
      let request = await fetch(
        "https://jstest-47ca2-default-rtdb.europe-west1.firebasedatabase.app/Cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );

      if (!request.ok) {
        throw new Error("Send cart data failed.");
      }
    };

    if (isInitial) {
      isInitial = false;
      return;
    }

    sendCartData().catch((error) => {});
  }, [cart]);
  return (
    <Layout>
      {isCartOpen && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
