import { createSlice } from "@reduxjs/toolkit";

let defaultState = {
  isOpen: false,
};

let cartSlice = createSlice({
  name: "cart",
  initialState: defaultState,
  reducers: {
    open(state) {
      state.isOpen = true;
    },
    close(state) {
      state.isOpen = false;
    },
  },
});

export default cartSlice;
