import { redirect } from "react-router-dom";

const getToken = () => {
  let token = localStorage.getItem("token");
  return token;
};

export const checkAuthLoader = () => {
  const token = getToken();

  if (!token) {
    return redirect("/authentication");
  }
};

export default getToken;
