import { useState } from "react";

const SimpleInput = (props) => {
  let [nameInput, setNameInput] = useState("");
  let [touchedName, setTouchedName] = useState(false);
  let [emailInput, setEmailInput] = useState("");
  let [touchedEmail, setTouchedEmail] = useState(false);

  let enteredNameisValid = nameInput.trim() === "";
  // let enteredEmailIsValid = ;
  let isValidName = enteredNameisValid && touchedName;
  let formisValid = false;

  if (isValidName) {
    formisValid = true;
  }

  const blurEmailHandler = () => {
    setTouchedEmail(true);
  };

  const emailChangeHandler = (event) => {
    setEma(event.target.value);
    setTouchedEmail(true);
  };

  const blurHandler = () => {
    setTouchedName(true);
  };

  const nameChangeHandler = (event) => {
    setNameInput(event.target.value);
    setTouchedName(true);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    setTouchedName(true);
  };

  let inputNameFieldClasses = isValidName
    ? "invalid form-control"
    : "form-control";
  let inputEmailFieldClasses = isValidEamil
    ? "invalid form-control"
    : "form-control";

  return (
    <form onSubmit={submitHandler}>
      <div className={inputNameFieldClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          onBlur={blurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="name"
        />
      </div>
      <div className={inputEmailFieldClasses}>
        <label htmlFor="name">Email</label>
        <input
          onBlur={blurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="name"
        />
      </div>
      <div className="form-actions">
        {formisValid ? (
          <button disabled="true" className="disabled">
            Submit
          </button>
        ) : (
          <button>Submit</button>
        )}
      </div>
      {isValidName && <p className="error-text">Error!</p>}
    </form>
  );
};

export default SimpleInput;
