import { useState } from "react";

const SimpleInput = (props) => {
  let [nameInput, setNameInput] = useState("");
  let [touchedName, setTouchedName] = useState(false);
  let [emailInput, setEmailInput] = useState("");
  let [touchedEmail, setTouchedEmail] = useState(false);

  let enteredNameisValid = nameInput.trim() !== "";
  let isValidName = enteredNameisValid && touchedName;

  let enteredEmailIsValid = emailInput.trim().includes("@");
  let isValidEmail = enteredEmailIsValid && touchedEmail;
  let formisValid = false;

  if (isValidName && isValidEmail) {
    formisValid = true;
  }

  const blurEmailHandler = () => {
    setTouchedEmail(true);
  };

  const emailChangeHandler = (event) => {
    setEmailInput(event.target.value);
    setTouchedEmail(true);
  };

  const blurHandler = () => {
    setTouchedName(true);
  };

  const nameChangeHandler = (event) => {
    setNameInput(event.target.value);
    setTouchedName(true);
  };

  const submitHandler = (event) => {
    event.preventDefault();

    setTouchedName(true);
    setTouchedEmail(true);
  };

  let inputNameFieldClasses =
    !isValidName && touchedName ? "invalid form-control" : "form-control";
  let inputEmailFieldClasses =
    !isValidEmail && touchedEmail ? "invalid form-control" : "form-control";
  let notErrorMessage =
    isValidEmail && isValidName && !touchedEmail && !touchedName;

  console.log(
    isValidEmail,
    touchedEmail,
    isValidName,
    touchedName,
    "Not Error:",
    notErrorMessage
  );
  return (
    <form onSubmit={submitHandler}>
      <div className={inputNameFieldClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          onBlur={blurHandler}
          onChange={nameChangeHandler}
          type="text"
          id="name"
        />
      </div>
      <div className={inputEmailFieldClasses}>
        <label htmlFor="name">Email</label>
        <input
          onBlur={blurEmailHandler}
          onChange={emailChangeHandler}
          type="text"
          id="name"
        />
      </div>
      <div className="form-actions">
        {!formisValid ? (
          <button disabled={true} className="disabled">
            Submit
          </button>
        ) : (
          <button>Submit</button>
        )}
      </div>
      {!notErrorMessage && <p className="error-text">Error!</p>}
    </form>
  );
};

export default SimpleInput;
